<?php

namespace ProAc\Icons;

use ProAc\SocialNetworks;

class Svg {

    static public function get_wanted_icons(){
        $buttons = SocialNetworks::get_selected_buttons();

        $icons = self::always_used();

        $shares = [];
        if(is_singular()){

            $shares = explode( ",", $buttons['share_buttons'] );
        }


        $follows = [];
        foreach(array_keys( self::icons() ) as $network) {

            if(isset($buttons["follow_button_".$network])){

                $follows [] = $network;
            }
        }


        return array_unique(array_merge($icons,$shares,$follows), SORT_REGULAR);
    }

    static public function always_used(){

        $base = [ 'search', 'comment-fill' ];

        if ( is_home() || is_front_page() || is_archive() ){
            $base [] = 'video-play';
        }

        if ( is_singular() ) {
            $base [] = 'share';
        }

        return apply_filters('always-used-icons', $base);
    }

    static public function get_available_icon($name){
        if(isset(self::icons()[$name])){
            return self::icons()[$name];
        }
        else {
            return false;
        }

    }
